import React, { useState } from 'react';

const images = [
  'images/main-image.png',
  'images/IMG_2015.jpg',
];


function ImageCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-900">
      <div className="relative w-5/6 h-5/6">
        {/* Previous Image */}
        <img
          src={images[(currentIndex - 1 + images.length) % images.length]}
          alt="Previous"
          className="absolute object-contain w-5/6 h-5/6 opacity-50 transform translate-x-[-10%] translate-y-[10%] z-0"
        />
        {/* Next Image */}
        <img
          src={images[(currentIndex + 1) % images.length]}
          alt="Next"
          className="absolute object-contain w-5/6 h-5/6 opacity-50 transform translate-x-[10%] translate-y-[10%] z-0"
        />
        {/* Current Image */}
        <img
          src={images[currentIndex]}
          alt="Current"
          className="object-contain w-full h-full rounded-lg relative z-10 shadow-lg"
        />
        <button
          onClick={handlePrev}
          className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-gray-700 text-white p-3 rounded-full hover:bg-gray-800 focus:outline-none z-20"
        >
          <span className="fas fa-arrow-left"></span>
        </button>
        <button
          onClick={handleNext}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-gray-700 text-white p-3 rounded-full hover:bg-gray-800 focus:outline-none z-20"
        >
          <span className="fas fa-arrow-right"></span>
        </button>
      </div>
    </div>
  );
}

export default ImageCarousel;
