import React, { useState, useEffect } from 'react';
import '../index.css';
import { Button } from './Button';
import '../App.css';
import ImageCarousel from './ImageCarousel';

function Main() {
  const initialText = "Hi ðŸ‘‹!";
  const finalText = "I'm Dev Patel";
  const [displayedText, setDisplayedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  useEffect(() => {
    let typingTimeout;

    if (!isDeleting && displayedText === initialText) {
      typingTimeout = setTimeout(() => {
        setIsDeleting(true);
        setTypingSpeed(100);
      }, 1000); // Delay before starting to delete
    } else if (isDeleting && displayedText === "") {
      setIsDeleting(false);
      setDisplayedText(finalText.substring(0, 1));
      setLoopNum(loopNum + 1);
      setTypingSpeed(150);
    } else {
      typingTimeout = setTimeout(() => {
        const currentText = isDeleting
          ? initialText
          : finalText;
        const updatedText = isDeleting
          ? currentText.substring(0, displayedText.length - 1)
          : currentText.substring(0, displayedText.length + 1);

        setDisplayedText(updatedText);
      }, typingSpeed);
    }

    return () => clearTimeout(typingTimeout);
  }, [displayedText, isDeleting]);

  return (
    <div className='relative flex flex-col flex-1 h-screen overflow-hidden bg-[#242422]'>
      <section className='relative grid grid-cols-1 lg:grid-cols-2 h-full'>
        <div className='bg-[#082f49] flex flex-col justify-center text-center lg:text-left gap-6 md:gap-8 lg:gap-10 p-6 lg:p-10 h-full'>
          <h2 className='font-bold text-4xl sm:text-5xl md:text-6xl text-white'>
            {displayedText}
          </h2>
          <p className='text-base sm:text-lg md:text-xl text-white'>
            Computer Science | <span className='text-sky-400'>UNC </span>| Business
          </p>
          <Button buttonStyle='btn--primary' buttonSize='btn--large'>
            DISCOVER MORE
          </Button>
        </div>
        <div className='relative bg-cover bg-center h-full flex items-center justify-center'>
          <ImageCarousel />
        </div>
      </section>
    </div>
  );
}

export default Main;
