import timelineElements from "../assets/timelineElements";
import schoolIcon from "../assets/school.svg";
import workIcon from "../assets/work.svg";

export default function Timeline({ defaultColor }) {
    return (
      <div>
        {timelineElements.map((element) => {
          const colors = [
            "bg-red-500",
            "bg-blue-500",
            "bg-yellow-500",
            "bg-purple-500",
            "bg-orange-500",
          ];

          const color = defaultColor || `bg-${element.color}-500`;
          const vibrantColor = defaultColor || `${element.color}-500`;

          return (
            <div key={element.id} className="flex m-4 relative">
              <div className="hidden items-start w-52 pt-0.5 relative sm:flex">
                <div className="w-4/5 text-gray-500">{element.date}</div>
                <div
                  className={`w-1 h-full translate-x-5 translate-y-10 opacity-100 bg-white`}
                ></div>
                <img
                  src={element.icon === "school" ? schoolIcon : workIcon}
                  alt="icon"
                  className={`${color} w-10 p-1 rounded-lg z-20`}
                />
                <div
                  className={`h-1 w-8 translate-y-5 opacity-100 bg-white`}
                ></div>
              </div>
              <div className="border border-gray-600 rounded-lg px-8 py-4 bg-gray-800 w-full text-center z-10 sm:mr-20 sm:text-left">
                <div className="text-xl font-medium">{element.title}</div>
                <div className="text-gray-300 mb-6 sm:mb-8 sm:text-xs">
                  {element.location}
                  <span className="sm:hidden">| {element.date}</span>
                </div>
                <div className="mb-4 text-left">{element.description}</div>
                <div className="flex flex-wrap mb-6 justify-center sm:justify-start">
                  {element.tech.map((tech, index) => {
                    return (
                      <span
                        key={index}
                        className="bg-gray-900 rounded-xl px-2 py-1 text-sm m-1"
                      >
                        {tech}
                      </span>
                    );
                  })}
                </div>
                <img
                  src={element.icon === "school" ? schoolIcon : workIcon}
                  alt="icon"
                  className={`${color} w-8 p-1 rounded-lg z-20 absolute left-4 top-4 sm:hidden m-2`}
                />
                <a
                  className={`${color} text-gray-950 font-medium px-4 py-1 rounded-md mx-auto cursor-pointer hover:text-wh sm:absolute left`}
                >
                  {element.buttonText}
                </a>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
