import React, { useState } from 'react';

const images = [
  'images/flower.jpeg',
  'images/IMG_2015.jpg',
  'main-image.png',
  'tree.jpg'
];

function ImageCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-900">
      <div className=" w-full max-w-4xl h-full object-contain object-cover object-scale-down">
        <img
          src={images[currentIndex]}
          alt="Carousel"
          className="object-cover w-full h-full"
        />
        <button
          onClick={handlePrev}
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-gray-700 text-white p-3 rounded-full hover:bg-gray-800 focus:outline-none"
        >
          <span className="fas fa-arrow-left"></span>
        </button>
        <button
          onClick={handleNext}
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-gray-700 text-white p-3 rounded-full hover:bg-gray-800 focus:outline-none"
        >
          <span className="fas fa-arrow-right"></span>
        </button>
      </div>
    </div>
  );
}

export default ImageCarousel;
