import React, { useState } from 'react';
import '../index.css';
import { Button } from './Button';
import '../App.css';
import './Main.css';
import ParticleBackground from './ParticleBackground';

const images = [
  "images/main-image.png",
  "images/IMG_2015.jpg",
  "images/flower.jpeg"
];

function Main() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className='relative flex flex-col flex-1 p-4 bg-[#242422]'>
      <ParticleBackground />
      <section className='grid grid-cols-1 lg:grid-cols-2 gap-10 py-8 sm:py-14 text-white'>
        <div className='flex flex-col lg:justify-center text-center lg:text-left gap-6 md:gap-8 lg:gap-10 sm:ml-17'>
          <h2 className='font-bold text-4xl sm:text-5xl md:text-6xl'>
            Hi! I'm <span className='text-sky-400'>Dev</span> Patel
            <br /> Full Stack
            <span className='text-sky-400'> Developer</span>
          </h2>
          <p className='text-base sm:text-lg md:text-xl text-gray-400'>
            Computer Science | <span className='text-sky-400'>UNC </span>| Business
          </p>
          <Button buttonStyle='btn--primary' buttonSize='btn--large'>
            DISCOVER MORE
          </Button>
        </div>
        <div className='relative shadow-2xl grid place-items-center'>
          <img
            src={images[currentIndex]}
            alt="Dev Patel"
            className='z-[2] max-h-[70vh]'
          />
          <button onClick={handlePrev} className='absolute left-0 bg-gray-700 text-white px-3 py-1 rounded-full hover:bg-gray-800'>Prev</button>
          <button onClick={handleNext} className='absolute right-0 bg-gray-700 text-white px-3 py-1 rounded-full hover:bg-gray-800'>Next</button>
        </div>
      </section>
    </div>
  );
}

export default Main;
