import React, { useState, useEffect } from 'react';
import '../index.css';
import { Button } from './Button';
import '../App.css';
import ImageCarousel from './ImageCarousel';

function Main() {
  const texts = [
    "Hi! I'm Dev Patel",
    "Full Stack Developer",
    "Computer Science | UNC | Business"
  ];

  const [currentTextIndex, setCurrentTextIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentTextIndex((prevIndex) => (prevIndex + 1) % texts.length);
    }, 3000); // Change text every 3 seconds

    return () => clearInterval(intervalId); // Cleanup interval on component unmount
  }, []);

  return (
    <div className='relative flex flex-col flex-1 h-screen overflow-hidden bg-[#242422]'>
      <section className='relative grid grid-cols-1 lg:grid-cols-2 h-full'>
        <div className='bg-[#082f49] flex flex-col justify-center text-center lg:text-left gap-6 md:gap-8 lg:gap-10 p-6 lg:p-10 h-full'>
          <h2 className='font-bold text-4xl sm:text-5xl md:text-6xl text-white'>
            {texts[currentTextIndex]}
          </h2>
          <p className='text-base sm:text-lg md:text-xl text-white'>
            Computer Science | <span className='text-sky-400'>UNC </span>| Business
          </p>
          <Button buttonStyle='btn--primary' buttonSize='btn--large'>
            DISCOVER MORE
          </Button>
        </div>
        <div className='relative bg-cover bg-center h-full flex items-center justify-center'>
          <ImageCarousel />
        </div>
      </section>
    </div>
  );
}

export default Main;
