import React from 'react';
import '../index.css';
import { Button } from './Button';
import '../App.css';
import './Main.css';
import ParticleBackground from './ParticleBackground';

function Main() {
  return (
    <div className='relative flex flex-col flex-1 p-4 bg-black'>
      <ParticleBackground />
      <section className='grid grid-cols-1 lg:grid-cols-2 gap-10 py-8 sm:py-14 text-white'>
        <div className='flex flex-col lg:justify-center text-center lg:text-left gap-6 md:gap-8 lg:gap-10'>
          <h2 className='font-bold text-4xl sm:text-5xl md:text-6xl'>
            Hi! I'm <span className='text-purple-500'>Samuel</span> Oak
            <br /> Full Stack
            <span className='text-purple-500'> Developer</span>
          </h2>
          <p className='text-base sm:text-lg md:text-xl text-gray-400'>
            My <span className='text-purple-500'>favorite tech</span> includes JavaScript (NEXT.JS or SvelteKit), TailwindCSS, Node.js + Express.js & PostgreSQL or Firebase/Firestore!
          </p>
          <Button buttonStyle='btn--primary' buttonSize='btn--large'>
            DISCOVER MORE
          </Button>
        </div>
        <div className='relative shadow-2xl grid place-items-center'>
          <img
            src={"images/main-image.png"}
            alt="Samuel Oak"
            className='object-cover z-[2] max-h-[70vh] rounded-full border-4 border-purple-500'
          />
        </div>
      </section>
    </div>
  );
}

export default Main;
